import React from 'react';
import { Paper, Typography, Box, Card, CardContent, Chip, Stack } from '@mui/material';
import { AccessTime, Speed, Settings } from '@mui/icons-material';

const formatArchitecture = (architecture) => {
  return architecture.map((layer, index) => {
    switch (layer.type) {
      case 'Convolution 2D':
        return `Conv2D:${layer.params.filters}:${layer.params.activation}`;
      case 'Max Pooling':
        return 'MaxPool';
      case 'Global Average Pooling':
        return 'GlobalAvgPool';
      case 'Flatten':
        return 'Flatten';
      case 'Dropout':
        return `Drop:${layer.params.rate}`;
      case 'Fully Connected':
        return `Dense:${layer.params.units}:${layer.params.activation}`;
      default:
        return layer.type;
    }
  }).join(' → ');
};

const formatDuration = (startTime, endTime) => {
  const duration = new Date(endTime) - new Date(startTime);
  const minutes = Math.floor(duration / 60000);
  const seconds = ((duration % 60000) / 1000).toFixed(0);
  return `${minutes}m ${seconds}s`;
};

const TrainingHistory = ({ history }) => {
  return (
    <Paper sx={{ p: 2, maxHeight: '80vh', overflow: 'auto' }}>
      <Typography variant="h6" gutterBottom>
        Training History
      </Typography>
      {history.map((entry, index) => (
        <Card key={entry.timestamp} sx={{ mb: 2, p: 1 }}>
          <Typography variant="subtitle2" gutterBottom>
            Model #{history.length - index} - {new Date(entry.timestamp).toLocaleString()}
          </Typography>

          <Stack direction="row" spacing={1} sx={{ mb: 1 }}>
            <Chip
              size="small"
              icon={<Settings />}
              label={`${entry.optimizer.toUpperCase()}`}
              color="primary"
              variant="outlined"
            />
            <Chip
              size="small"
              icon={<Speed />}
              label={`LR: ${entry.learning_rate}`}
              color="primary"
              variant="outlined"
            />
            <Chip
              size="small"
              icon={<AccessTime />}
              label={`${entry.epochs} epochs`}
              color="primary"
              variant="outlined"
            />
          </Stack>

          <Typography 
            variant="caption" 
            color="text.secondary" 
            sx={{ 
              display: 'block', 
              mb: 1,
              whiteSpace: 'normal',
              wordBreak: 'break-word'
            }}
          >
            {formatArchitecture(entry.architecture)}
          </Typography>

          <Box sx={{ 
            display: 'flex', 
            justifyContent: 'space-between', 
            alignItems: 'center',
            flexWrap: 'wrap',
            gap: 1
          }}>
            <Typography variant="body2" color="text.secondary">
              Train: {(entry.final_train_accuracy * 100).toFixed(2)}% | 
              Test: {(entry.final_test_accuracy * 100).toFixed(2)}%
            </Typography>
            <Typography variant="body2" color="text.secondary">
              Params: {entry.total_params.toLocaleString()}
            </Typography>
          </Box>
        </Card>
      ))}
      {history.length === 0 && (
        <Typography variant="body2" color="text.secondary" textAlign="center" py={4}>
          No training history yet
        </Typography>
      )}
    </Paper>
  );
};

export default TrainingHistory;
