import React from 'react';
import { Paper, Typography, Box, Card, CardContent } from '@mui/material';
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
} from 'recharts';

const formatArchitecture = (architecture) => {
  return architecture.map((layer, index) => {
    switch (layer.type) {
      case 'Convolution 2D':
        return `Conv2D:${layer.params.filters}:${layer.params.activation}`;
      case 'Max Pooling':
        return 'MaxPool';
      case 'Flatten':
        return 'Flatten';
      case 'Dropout':
        return `Dropout:${layer.params.rate}`;
      case 'Fully Connected':
        return `Dense:${layer.params.units}:${layer.params.activation}`;
      default:
        return layer.type;
    }
  }).join(' → ');
};

const TrainingHistory = ({ history }) => {
  return (
    <Paper sx={{ p: 2, maxHeight: '80vh', overflow: 'auto' }}>
      <Typography variant="h6" gutterBottom>
        Training History
      </Typography>
      {history.map((entry, index) => (
        <Card key={entry.timestamp} sx={{ mb: 2 }}>
          <CardContent>
            <Typography variant="subtitle2" gutterBottom>
              Model #{history.length - index} - {new Date(entry.timestamp).toLocaleString()}
            </Typography>
            <Typography variant="caption" color="text.secondary" sx={{ display: 'block', mb: 1 }}>
              {formatArchitecture(entry.architecture)}
            </Typography>
            <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 1 }}>
              <Typography variant="body2" color="text.secondary">
                Train: {(entry.final_train_accuracy * 100).toFixed(2)}% | 
                Test: {(entry.final_test_accuracy * 100).toFixed(2)}%
              </Typography>
              <Typography variant="body2" color="text.secondary">
                Params: {entry.total_params.toLocaleString()}
              </Typography>
            </Box>

            {/* Training Progress Graphs */}
            <Box sx={{ mt: 2 }}>
              <LineChart width={350} height={150} data={entry.training_results}>
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="epoch" />
                <YAxis />
                <Tooltip />
                <Legend />
                <Line
                  type="monotone"
                  dataKey="train_accuracy"
                  stroke="#8884d8"
                  name="Train"
                />
                <Line
                  type="monotone"
                  dataKey="test_accuracy"
                  stroke="#82ca9d"
                  name="Test"
                />
              </LineChart>
            </Box>
          </CardContent>
        </Card>
      ))}
      {history.length === 0 && (
        <Typography variant="body2" color="text.secondary" textAlign="center" py={4}>
          No training history yet
        </Typography>
      )}
    </Paper>
  );
};

export default TrainingHistory;
