import React, { useState } from 'react';
import { Container, Box, Grid } from '@mui/material';
import LayerPalette from './components/LayerPalette';
import NetworkBuilder from './components/NetworkBuilder';
import TrainingConfig from './components/TrainingConfig';
import TrainingResults from './components/TrainingResults';
import TrainingHistory from './components/TrainingHistory';
import './App.css';

function App() {
  const [layers, setLayers] = useState([]);
  const [trainingResults, setTrainingResults] = useState([]);
  const [isTraining, setIsTraining] = useState(false);

  const layerTypes = {
    conv2d: {
      type: 'Convolution 2D',
      icon: 'âš¡',
      defaultParams: {
        filters: 32,
        kernelSize: 3,
        stride: 1,
        padding: 1,
        activation: 'relu'
      }
    },
    maxpool: {
      type: 'Max Pooling',
      icon: 'ðŸ”²',
      defaultParams: {
        poolSize: 2,
        stride: 2
      }
    },
    flatten: {
      type: 'Flatten',
      icon: 'ðŸ“„',
      defaultParams: {}
    },
    dropout: {
      type: 'Dropout',
      icon: 'ðŸ’§',
      defaultParams: {
        rate: 0.25
      }
    },
    dense: {
      type: 'Fully Connected',
      icon: 'ðŸ”Œ',
      defaultParams: {
        units: 128,
        activation: 'relu'
      }
    }
  };

  const handleDrop = (e) => {
    e.preventDefault();
    const layerType = e.dataTransfer.getData('layerType');
    const layerTemplate = layerTypes[layerType];

    if (layerTemplate) {
      const newLayer = {
        id: `${layerType}-${Date.now()}`,
        type: layerTemplate.type,
        icon: layerTemplate.icon,
        defaultParams: { ...layerTemplate.defaultParams }
      };

      if (layerType === 'flatten') {
        if (layers.some(layer => layer.type === 'Flatten')) {
          return;
        }
        const hasConvBefore = layers.some(layer => 
          layer.type === 'Convolution 2D' || layer.type === 'Max Pooling'
        );
        const hasDenseAfter = layers.some(layer => layer.type === 'Fully Connected');
        if (!hasConvBefore || hasDenseAfter) {
          return;
        }
      }

      setLayers([...layers, newLayer]);
    }
  };

  const handleDragOver = (e) => {
    e.preventDefault();
  };

  const handleLayerUpdate = (index, params) => {
    const newLayers = [...layers];
    newLayers[index] = { ...newLayers[index], defaultParams: params };
    setLayers(newLayers);
  };

  const handleLayerDelete = (index) => {
    const newLayers = [...layers];
    newLayers.splice(index, 1);
    setLayers(newLayers);
  };

  return (
    <Container maxWidth="lg">
      <Box sx={{ my: 4 }}>
        <h1>MNIST Training Dashboard</h1>
        <LayerPalette />
        <Box
          onDrop={handleDrop}
          onDragOver={handleDragOver}
          sx={{ 
            minHeight: '200px',
            border: '2px dashed #ccc',
            borderRadius: '4px',
            mb: 2,
            p: 2
          }}
        >
          <NetworkBuilder
            layers={layers}
            onLayerUpdate={handleLayerUpdate}
            onLayerDelete={handleLayerDelete}
          />
        </Box>
        <TrainingConfig 
          networkLayers={layers}
          setTrainingResults={setTrainingResults}
          isTraining={isTraining}
          setIsTraining={setIsTraining}
        />
        <TrainingResults results={trainingResults} />
      </Box>
    </Container>
  );
}

export default App;
