import React, { useState } from 'react';
import { Paper, Typography, Box, Button } from '@mui/material';
import { Droppable } from 'react-beautiful-dnd';
import LayerCard from './LayerCard';

const NetworkBuilder = ({ layers, onLayerUpdate, onLayerDelete }) => {
  return (
    <Paper sx={{ p: 2 }}>
      <Typography variant="h6" gutterBottom>
        Network Architecture
      </Typography>
      <Droppable droppableId="networkBuilder">
        {(provided) => (
          <Box
            ref={provided.innerRef}
            {...provided.droppableProps}
            sx={{ minHeight: 200, p: 2, backgroundColor: '#f8f8f8' }}
          >
            {layers.map((layer, index) => (
              <LayerCard
                key={layer.id}
                layer={layer}
                onDelete={() => onLayerDelete(index)}
                onUpdate={(params) => onLayerUpdate(index, params)}
              />
            ))}
            {provided.placeholder}
          </Box>
        )}
      </Droppable>
    </Paper>
  );
};

export default NetworkBuilder;
