import React, { useState } from 'react';
import { Container, Box } from '@mui/material';
import { DragDropContext } from 'react-beautiful-dnd';
import LayerPalette from './components/LayerPalette';
import NetworkBuilder from './components/NetworkBuilder';
import TrainingConfig from './components/TrainingConfig';
import TrainingResults from './components/TrainingResults';
import './App.css';

function App() {
  const [layers, setLayers] = useState([]);
  const [trainingResults, setTrainingResults] = useState([]);
  const [isTraining, setIsTraining] = useState(false);

  const handleDragEnd = (result) => {
    if (!result.destination) return;

    const { source, destination } = result;

    // If dragging from palette to builder
    if (source.droppableId === 'layerPalette' && destination.droppableId === 'networkBuilder') {
      const layerType = result.draggableId.split('-')[0];
      const layerTypes = {
        conv2d: {
          id: `conv2d-${Date.now()}`,
          type: 'Convolution 2D',
          icon: 'âš¡',
          defaultParams: {
            filters: 32,
            kernelSize: 3,
            stride: 1,
            padding: 1,
            activation: 'relu'
          }
        },
        maxpool: {
          id: `maxpool-${Date.now()}`,
          type: 'Max Pooling',
          icon: 'ðŸ”²',
          defaultParams: {
            poolSize: 2,
            stride: 2
          }
        },
        dropout: {
          id: `dropout-${Date.now()}`,
          type: 'Dropout',
          icon: 'ðŸ’§',
          defaultParams: {
            rate: 0.25
          }
        },
        dense: {
          id: `dense-${Date.now()}`,
          type: 'Fully Connected',
          icon: 'ðŸ”Œ',
          defaultParams: {
            units: 128,
            activation: 'relu'
          }
        }
      };

      const newLayer = layerTypes[layerType];
      if (newLayer) {
        const newLayers = [...layers];
        newLayers.splice(destination.index, 0, newLayer);
        setLayers(newLayers);
      }
    }
    // If reordering within builder
    else if (source.droppableId === 'networkBuilder' && destination.droppableId === 'networkBuilder') {
      const newLayers = [...layers];
      const [removed] = newLayers.splice(source.index, 1);
      newLayers.splice(destination.index, 0, removed);
      setLayers(newLayers);
    }
  };

  const handleLayerUpdate = (index, params) => {
    const newLayers = [...layers];
    newLayers[index] = { ...newLayers[index], defaultParams: params };
    setLayers(newLayers);
  };

  const handleLayerDelete = (index) => {
    const newLayers = [...layers];
    newLayers.splice(index, 1);
    setLayers(newLayers);
  };

  return (
    <Container maxWidth="lg">
      <Box sx={{ my: 4 }}>
        <h1>MNIST Training Dashboard</h1>
        <DragDropContext onDragEnd={handleDragEnd}>
          <LayerPalette />
          <NetworkBuilder
            layers={layers}
            onLayerUpdate={handleLayerUpdate}
            onLayerDelete={handleLayerDelete}
          />
        </DragDropContext>
        <TrainingConfig 
          networkLayers={layers}
          setTrainingResults={setTrainingResults}
          isTraining={isTraining}
          setIsTraining={setIsTraining}
        />
        <TrainingResults results={trainingResults} />
      </Box>
    </Container>
  );
}

export default App;
