import React from 'react';
import { Paper, Typography, Box } from '@mui/material';
import { Droppable, Draggable } from 'react-beautiful-dnd';
import LayerCard from './LayerCard';

const NetworkBuilder = ({ layers, onLayerUpdate, onLayerDelete }) => {
  return (
    <Paper sx={{ p: 2 }}>
      <Typography variant="h6" gutterBottom>
        Network Architecture
      </Typography>
      <Droppable droppableId="networkBuilder">
        {(provided) => (
          <Box
            ref={provided.innerRef}
            {...provided.droppableProps}
            sx={{ minHeight: 200, p: 2, backgroundColor: '#f8f8f8' }}
          >
            {layers.map((layer, index) => (
              <Draggable
                key={layer.id}
                draggableId={layer.id}
                index={index}
              >
                {(provided, snapshot) => (
                  <div
                    ref={provided.innerRef}
                    {...provided.draggableProps}
                    {...provided.dragHandleProps}
                    style={{
                      ...provided.draggableProps.style,
                      opacity: snapshot.isDragging ? 0.5 : 1
                    }}
                  >
                    <LayerCard
                      layer={layer}
                      onDelete={() => onLayerDelete(index)}
                      onUpdate={(params) => onLayerUpdate(index, params)}
                    />
                  </div>
                )}
              </Draggable>
            ))}
            {provided.placeholder}
          </Box>
        )}
      </Droppable>
    </Paper>
  );
};

export default NetworkBuilder;
